import csvimport osimport randombase_dir = os.path.dirname(os.path.abspath(__file__))Input_file = os.path.join("data", "amazon_inventory.csv")Day1_file = os.path.join("data", "inventory_day1.csv")Day2_file = os.path.join("data", "inventory_day2.csv")price_change_rate = 0.15 # 15% of products get price changesremoval_rate = 0.05 # 5% of products are removednew_product_count = 20 # New products added# -------------- ## LOAD OG DATA# -------------- #with open(Input_file, newline="", encoding="utf-8") as f:    reader = csv.DictReader(f)    header = reader.fieldnames    rows = list(reader)# -------------- ## DAY 1 SNAPSHOT# -------------- #with open(Day1_file, "w", newline="", encoding="utf-8") as f:    writer = csv.DictWriter(f, fieldnames=header)    writer.writeheader()    writer.writerows(rows)# -------------- ## DAY 2 SNAPSHOT# -------------- #day2_rows = rows.copy()# 1. Remove some productsremove_count = int(len(day2_rows) * removal_rate)removed_products = set(random.sample([row["product_id"] for row  in day2_rows], remove_count)                       )day2_rows = [    row for row in day2_rows    if row["product_id"] not in removed_products]# 2. Modify pricesprice_change_count  = int(len(day2_rows) * price_change_rate)products_to_update = random.sample(day2_rows, price_change_count)for row in products_to_update:    try:        price = float(row["discounted_price"].replace("₹", "").replace(",", ""))        change_factor = random.uniform(0.9, 1.1) # ±10%        new_price = round(price * change_factor, 2)        row["discounted_price"] = f"₹{new_price}"    except:        continue    # 3. Add new productsfor i in range(new_product_count):    new_product = {        "product_id": f"NEW_{i+1}",        "product_name": f"New Product {i+1}",        "category": "Misc",        "discounted_price": "₹499",        "actual_price": "₹699",        "discount_percentage": "29%",        "rating": "0",        "rating_count": "0",        "about_product": "Newly added product",        "user_id": "",        "user_name": "",        "review_id": "",        "review_title": "",        "review_content": "",        "img_link": "",        "product_link": ""    }    day2_rows.append(new_product)    # Write Day 2 CSVwith open(Day2_file, "w", newline="", encoding="utf-8") as f:    writer = csv.DictWriter(f, fieldnames=header)    writer.writeheader()    writer.writerows(day2_rows)print("Inventory snapshots created:")print("- inventory_day1.csv")print("- inventory_day2.csv")        